script:
frappe.ui.form.on('Event', {
    refresh: function(frm) {
        // Toggle amount field visibility
        frm.toggle_display("event_fee", frm.doc.is_paid_event);
    },
    is_paid_event: function(frm) {
        // Show/hide amount field when checkbox changes
        frm.toggle_display("event_fee", frm.doc.is_paid_event);
        if (!frm.doc.is_paid_event) {
            frm.set_value("event_fee", null);
        }
    }
});

frappe.ui.form.on('Collection Tool', {
    refresh: function(frm) {
        // Toggle department field visibility
        frm.toggle_display("department", frm.doc.is_department_specific);
    },
    is_department_specific: function(frm) {
        // Show/hide department field when checkbox changes
        frm.toggle_display("department", frm.doc.is_department_specific);
        if (!frm.doc.is_department_specific) {
            frm.set_value("department", null);
        }
    }
});

frappe.ui.form.on("Event or Program Attendance TOOL", {
    refresh: function(frm) {
        // Event query
        frm.set_query("event_program_name", function() {
            return {
                filters: {
                    "status": "Open",
                    "ends_on": [">", frappe.datetime.nowdate()]
                }
            };
        });

        // Branch query
        frm.set_query("event_branch", function() {
            return {
                filters: {
                    "disabled": 0
                }
            };
        });
    }
});

frappe.listview_settings["Church Members"] = {
    onload: function (list_view) {
        list_view.page.add_inner_button(__("Register or Remove Member from Group/Ministry"), function () {
            let dialog = new frappe.ui.Dialog({
                title: __("Register or Remove Member from Group/Ministry"),
                fields: [
                    {
                        fieldtype: "Link",
                        label: __("Member"),
                        fieldname: "member",
                        options: "Church Members",
                        reqd: 1,
                        get_query: function () {
                            return {
                                filters: {
                                    membership_status: "Active"
                                }
                            };
                        },
                        onchange: function () {
                            check_and_update_button();
                        }
                    },
                    {
                        fieldtype: "Link",
                        label: __("Group/Ministry"),
                        fieldname: "group_ministry",
                        options: "Church Groups and Ministries",
                        reqd: 1,
                        get_query: function () {
                            return {
                                filters: {
                                    group_ministry_status: "Active"
                                }
                            };
                        },
                        onchange: function () {
                            check_and_update_button();
                        }
                    }
                ],
                primary_action_label: __("Register"),
                primary_action: function () {
                    // This will be dynamically set by check_and_update_button
                }
            });

            // Initially set the primary action to "Register"
            dialog.set_primary_action(__("Register"), function () {
                register_member();
            });

            // Function to check membership and update the button
            function check_and_update_button() {
                let member = dialog.get_value("member");
                let group_ministry = dialog.get_value("group_ministry");

                if (member && group_ministry) {
                    frappe.call({
                        method: "cfms_plus.cfms_plus.doctype.church_members.church_members.check_member_in_group",
                        args: {
                            member: member,
                            group_ministry: group_ministry
                        },
                        callback: function (r) {
                            if (r.message) {
                                // Member is already in the group
                                dialog.set_primary_action(__("Remove"), function () {
                                    remove_member();
                                });
                                dialog.get_primary_btn()
                                    .removeClass("btn-primary")
                                    .addClass("btn-danger");
                            } else {
                                // Member is not in the group
                                dialog.set_primary_action(__("Register"), function () {
                                    register_member();
                                });
                                dialog.get_primary_btn()
                                    .removeClass("btn-danger")
                                    .addClass("btn-primary");
                            }
                        }
                    });
                }
            }

            // Function to register a member
            function register_member() {
                let values = dialog.get_values();
                frappe.call({
                    method: "cfms_plus.cfms_plus.doctype.church_members.church_members.register_member_to_group_ministry",
                    args: {
                        member: values.member,
                        group_ministry: values.group_ministry
                    },
                    callback: function (r) {
                        if (!r.exc) {
                            dialog.hide();
                            list_view.refresh();
                        }
                    }
                });
            }

            // Function to remove a member
            function remove_member() {
                let values = dialog.get_values();
                frappe.call({
                    method: "cfms_plus.cfms_plus.doctype.church_members.church_members.remove_member_from_group_ministry",
                    args: {
                        member: values.member,
                        group_ministry: values.group_ministry
                    },
                    callback: function (r) {
                        if (!r.exc) {
                            dialog.hide();
                            list_view.refresh();
                        }
                    }
                });
            }

            dialog.show();
        }).addClass("btn-primary");
    }
};

