// public/js/church_events_and_programs_calendar.js

frappe.views.calendar["Church Events and Programs"] = {
    doctype: "Church Events and Programs",

    // Month/Week/Day Calendar
    field_map: {
        start:       "start",
        end:         "end",
        id:          "id",
        title:       "title",
        color:       "color",
        allDay:      "allDay",
        description: "description"
    },
    get_events_method:
      "cfms_plus.cfms_plus.doctype.church_events_and_programs.church_events_and_programs.get_events",

    // disable Gantt
    gantt: false,

    // **Gantt** needs these exact keys:
    gantt_field_map: {
        id:     "id",                 // each record’s unique id
        name:   "title",              // the bar’s label in Gantt
        start:  "start",              // start date
        end:    "end",                // end date
        color:  "color"               // bar colour
    },

    // filters: [
    //   {
    //     fieldtype: "Link",
    //     fieldname: "group_ministry",
    //     options: "Group Ministry",
    //     label: __("Group Ministry")
    //   }
    // ],

    options: {
      eventRender: function(eventObj, $el) {
        if (eventObj.description) {
          $el.find(".fc-title")
             .append(`<div class="fc-event-desc small">${eventObj.description}</div>`);
        }
      }
    }
};
